    u8 buffer[8] = {
        0x01,0x55,0x02,0xaa,
        0x99,0x72,0x18,0x56
    };
    u16 crc;
    int i;

/* Run sample message through check routine. */
crc = 0;
for (i = 0; i < 8; i++)
    crc = crc16_t8(crc ^ (buffer[i]<<8));

    /* Output is 0000 64 -- no error encountered. */
    printf("\nError test:  CRC %04X, bit position %d\n",
