-- Abstraction of a fstab entry (a "mount").
-- These lookups would likely be performed by an
-- an automounter type application.
mount   OBJECT-CLASS ::= {
        SUBCLASS OF { top }
        MUST CONTAIN { mountHost |
                       mountDirectory |
                       mountType
                     }
        MAY CONTAIN { mountOption |
                      mountDumpFrequency |
                      mountPassNo
                    }
        ID { <oid1> }
}
