$top = 2001;
$hswin = 1;
$regwin = 1;
$rtt = 1;
$lastrtt = 0;
$rttstep = 100;
if ($hswin == 1) {
  print "  RTT  HS_Window Standard_TCP_Window0;
  print "  ---  --------- -------------------0;
}
while ($rtt < $top) {
  $bw = (0.1 -0.5)*(log($hswin)-log(38))/(log(83000)-log(38))+0.5;
  $aw = ($hswin**2*2.0*$bw) / ((2.0-$bw)*$hswin**1.2*12.8);
  if ($aw < 1) {
      $aw = 1;
  }
  if ($rtt >= $lastrtt + $rttstep) {
    printf "%5d %9d %10d0, $rtt, $hswin, $regwin;
    $lastrtt = $rtt;
  }
  $hswin += $aw;
  $regwin += 1;
  $rtt ++;
}
