contents.text:
object {
  IncrementalUpdateMediaTypes incremental-change-media-types;
} TIPSCapabilities;

object-map {
   ResourceID -> String;
} IncrementalUpdateMediaTypes;

"my-network-map": {
  "uri": "https://alto.example.com/networkmap",
  "media-type": "application/alto-networkmap+json"
},
"my-routingcost-map": {
  "uri": "https://alto.example.com/costmap/routingcost",
  "media-type": "application/alto-costmap+json",
  "uses": ["my-network-map"],
  "capabilities": {
    "cost-type-names": ["num-routingcost"]
  }
},
"my-hopcount-map": {
  "uri": "https://alto.example.com/costmap/hopcount",
  "media-type": "application/alto-costmap+json",
  "uses": ["my-network-map"],
  "capabilities": {
    "cost-type-names": ["num-hopcount"]
  }
},
"my-simple-filtered-cost-map": {
  "uri": "https://alto.example.com/costmap/filtered/simple",
  "media-type": "application/alto-costmap+json",
  "accepts": "application/alto-costmapfilter+json",
  "uses": ["my-network-map"],
  "capabilities": {
    "cost-type-names": ["num-routingcost", "num-hopcount"],
    "cost-constraints": false
  }
},
"update-my-costs": {
  "uri": "https://alto.example.com/updates/costs",
  "media-type": "text/event-stream",
  "accepts": "application/alto-updatestreamparams+json",
  "uses": [
      "my-network-map",
      "my-routingcost-map",
      "my-hopcount-map",
      "my-simple-filtered-cost-map"
  ],
  "capabilities": {
    "incremental-change-media-types": {
      "my-network-map": "application/json-patch+json",
      "my-routingcost-map": "application/merge-patch+json",
      "my-hopcount-map": "application/merge-patch+json"
    },
    "support-stream-control": true
  }
},
"update-my-costs-tips": {
  "uri": "https://alto.example.com/updates-new/costs",
  "media-type": "application/alto-tips+json",
  "accepts": "application/alto-tipsparams+json",
  "uses": [
      "my-network-map",
      "my-routingcost-map",
      "my-hopcount-map",
      "my-simple-filtered-cost-map"
  ],
  "capabilities": {
    "incremental-change-media-types": {
      "my-network-map": "application/json-patch+json",
      "my-routingcost-map": "application/merge-patch+json",
      "my-hopcount-map": "application/merge-patch+json",
      "my-simple-filtered-cost-map": "application/merge-patch+json"
    }
  }
},
"tips-sse": {
  "uri": "https://alto.example.com/updates/tips",
  "media-type": "text/event-stream",
  "accepts": "application/alto-updatestreamparams+json",
  "uses": [ "update-my-costs-tips" ],
  "capabilities": {
    "incremental-change-media-types": {
      "update-my-costs-tips": "application/merge-patch+json"
    }
  }
}

object {
   ResourceID   resource-id;
   [JSONString  tag;]
   [Object      input;]
} TIPSReq;

object {
  URI               tips-view-uri;
  TIPSViewSummary   tips-view-summary;
} AddTIPSResponse;

object {
  UpdatesGraphSummary   updates-graph-summary;
} TIPSViewSummary;

object {
  JSONNumber       start-seq;
  JSONNumber       end-seq;
  StartEdgeRec     start-edge-rec;
} UpdatesGraphSummary;

object {
  JSONNumber       seq-i;
  JSONNumber       seq-j;
} StartEdgeRec;

HTTP/1.1 400 Bad Request
Content-Length: 131
Content-Type: application/alto-error+json

{
    "meta":{
        "code":  "E_INVALID_FIELD_VALUE",
        "field": "resource-id",
        "value": "my-network-map/#"
    }
}

HTTP/1.1 200 OK
Content-Type: application/alto-tips+json
Content-Length: 255

{
  "tips-view-uri": "https://alto.example.com/tips/2718281828",
  "tips-view-summary": {
    "updates-graph-summary": {
      "start-seq": 101,
      "end-seq": 106,
      "start-edge-rec" : {
        "seq-i": 0,
        "seq-j": 105
      }
    }
  }
}

POST /updates/tips HTTP/1.1
Host: alto.example.com
Accept: text/event-stream,application/alto-error+json
Content-Type: application/alto-updatestreamparams+json
Content-Length: 76

{
  "add": {
    "tips-123": { "resource-id": "update-my-cost-tips" }
  }
}

HTTP/1.1 200 OK
Connection: keep-alive
Content-Type: text/event-stream

event: application/alto-tips+json,tips-123
data: {
data:   "tips-view-uri": "https://alto.example.com/tips/2718281828",
data:   "tips-view-summary": {
data:     "updates-graph-summary": {
data:       "start-seq": 101,
data:       "end-seq": 106,
data:       "start-edge-rec" : {
data:         "seq-i": 0,
data:         "seq-j": 105
data:       }
data:     }
data:   }
data: }

event: application/merge-patch+json,tips-123
data: {
data:   "tips-view-summary": {
data:     "updates-graph-summary": {
data:       "end-seq": 107
data:     }
data:   }
data: }

HTTP/1.1 200 OK
Content-Type: application/merge-patch+json
Content-Length: 193

{
  "tips-view-summary": {
    "updates-graph-summary": {
      "start-seq": 101,
      "end-seq": 106,
      "start-edge-rec": {
        "seq-i": 103,
        "seq-j": 105
      }
    }
  }
}
